<br />		while (input.hasNext()) {
<br />			int job = input.nextInt();
<br />			int level = input.nextInt();
<br />
<br />			System.out.println(countSkillPoint(job, level));
<br />		}
<br />	}
<br />
<br />	public static int countSkillPoint(int job, int level) {
<br />		int skillPoint = 0;
<br />		
<br />		switch (job) {
<br />		case 0:
<br />			break;
<br />		case 2:
<br />			skillPoint = (level - 8) * 3 + countPlusPoint(level);
<br />			break;
<br />		default:
<br />			skillPoint = (level - 10) * 3 + countPlusPoint(level);
<br />			break;
<br />		}
<br />		
<br />		return skillPoint;
<br />	}
<br />	
<br />	public static int countPlusPoint(int level){
<br />		int plusPoint = 0;
<br />		
<br />		if(level &lt; 30){
<br />			plusPoint = 1;
<br />		}
<br />		else if(level &lt; 70){
<br />			plusPoint = 2;
<br />		}
<br />		else if(level &lt; 120){
<br />			plusPoint = 3;
<br />		}
<br />		else{
<br />			plusPoint = 6;
<br />		}
<br />		
<br />		return plusPoint;
<br />	}
<br />
<br />}
