<br />
<br />public class t1083 {
<br />
<br />	public static void main(String[] args) {
<br />		Scanner input = new Scanner(System.in);
<br />
<br />		while (input.hasNext()) {
<br />			int n = input.nextInt();
<br />			int k = input.next().length();
<br />
<br />			System.out.println(factorials(n, k));
<br />		}
<br />	}
<br />	
<br />	public static int factorials(int n, int k){
<br />		int result = 1;
<br />		
<br />		if((n % k) == 0){
<br />			for(int i = n; i &gt;= k; i -= k){
<br />				result *= i;
<br />			}
<br />		}
<br />		else{
<br />			for(int i = n; i &gt;= (n % k); i -= k){
<br />				result *= i;
<br />			}
<br />		}
<br />		
<br />		return result;
<br />	}
<br />}
